mapper.select:
SELECT  d.owner,d.name,d.type,d.line,d.text FROM dba_source  D
WHERE D.NAME= #{objectName}
  AND D.OWNER= #{owner}
  AND D.TYPE='PACKAGE'
ORDER BY LINE

SELECT  d.owner,d.name,d.type,d.line,d.text FROM dba_source  D
WHERE D.NAME= #{objectName}
  AND D.OWNER= #{owner}
  AND D.TYPE='PACKAGE BODY'
ORDER BY LINE
