mapper.select:
select d.owner "owner", d.index_name "objectName", d.index_type "objectType"
from dba_indexes d
where 1 = 1
  AND D.TABLE_NAME = #{objectName}
  AND D.TABLE_OWNER = #{owner}
  AND D.TABLE_TYPE = #{objectType}

SELECT d.owner, d.name, d.type, d.line, d.text
FROM dba_source D
WHERE D.NAME = #{objectName}
  AND D.OWNER = #{owner}
  AND D.TYPE = 'PACKAGE'
ORDER BY LINE

SELECT d.owner, d.name, d.type, d.line, d.text
FROM dba_source D
WHERE D.NAME = #{objectName}
  AND D.OWNER = #{owner}
  AND D.TYPE = 'PACKAGE BODY'
ORDER BY LINE
